
DROP TABLE IF EXISTS public.history;
DROP TABLE IF EXISTS public.user;

--users of the website who may be office staff or occupants
CREATE TABLE public.user
(
    user_id SERIAL PRIMARY KEY,
    first_name character varying(50),
    last_name character varying(50),
    email character varying(100),
	phone character varying(100),
	user_name character varying(100)  NOT NULL,
	password character varying(1000),
	notes text
  
);




SELECT setval(pg_get_serial_sequence('public.user', 'user_id'), (SELECT MAX(user_id) FROM public.user)+1);


CREATE TABLE public.history
(
 history_id SERIAL PRIMARY KEY, 
 user_id bigint,
 stock_id bigint,
 date_trade date,
 stock_price double precision,
 stock_quantity_owned bigint,
 cash double precision,
 notes text,
  
	  CONSTRAINT user_id FOREIGN KEY (user_id)
	REFERENCES public.user (user_id) MATCH SIMPLE
	ON UPDATE NO ACTION
	ON DELETE NO ACTION
);


		






